class Solution:
    def findMaxFish(self, grid: List[List[int]]) -> int:
        """
        simple dfs should suffice
        """
        m, n = len(grid), len(grid[0])
        self.max_fish = 0

        def is_valid(i, j):
            if 0 <= i < m and 0 <= j < n:
                return True
            return False

        def get_neis(i, j):
            return [(i+1,j), (i-1, j), (i,j-1), (i,j+1)]

        def dfs(i, j, dfs_visited):
            self.count+=grid[i][j]
            dfs_visited.add((i,j))
            self.max_fish = max(self.max_fish, self.count)
            for nei in get_neis(i, j):
                x, y = nei
                if is_valid(x, y) and grid[x][y] != 0 and (x, y) not in dfs_visited:
                    dfs(x, y, dfs_visited)

        self.visited = set()
        for i in range(m):
            for j in range(n):
                if grid[i][j] != 0 and (i,j) not in self.visited:
                    dfs_visited = set()
                    self.count = 0
                    dfs(i, j, dfs_visited)
                    self.visited = self.visited.union(dfs_visited)

        return self.max_fish

