public:
    bool valid(int i,int j,int n,int m){
      return i>-1 && i<n && j>-1 && j<m;
    }
    void Bfs(int i,int j,int &count,vector<vector<int>>& grid){
      int n=grid.size();
      int m=grid[0].size();
      int row[4]={-1,1,0,0};
      int col[4]={0,0,-1,1};
      queue<pair<int,int>>q;
      q.push({i,j});
      while(!q.empty()){
        i=q.front().first;
        j=q.front().second;
        q.pop();
        for(int k=0;k<4;k++){
          if(valid(i+row[k],j+col[k],n,m) && grid[i+row[k]][j+col[k]]>0){
            q.push({i+row[k],j+col[k]});
            count+=grid[i+row[k]][j+col[k]];
            grid[i+row[k]][j+col[k]]=0;
          }
        }
      }
    }
    int findMaxFish(vector<vector<int>>& grid) {
      int ans=0;
      int n=grid.size();
      int m=grid[0].size();
      for(int i=0;i<n;i++){
        for(int j=0;j<m;j++){
          int count=0;
           if(grid[i][j]>0){
              count+=grid[i][j];
              grid[i][j]=0;
              Bfs(i,j,count,grid);
              ans=max(ans,count);

           }
        }
      } 
      return ans; 
    }
};
