private:
    int dfs(int row,int col,vector<vector<int>>&vis,vector<vector<int>>&grid){
        // base case that makes the curr cell invalid, if visited, or if it 
        //is land cell we return 0 (no fish caught);
        int m = grid.size();
        int n = grid[0].size();

        if(row<0 || row>=m || col<0 || col>=n || vis[row][col] || 
        grid[row][col]==0)
        return 0;

        //otherwise we mark it as visited and proceed
        vis[row][col] = 1;

        int res = 0;
        for(int i=0;i<4;i++){
            int nrow = row + delrow[i];
            int ncol = col + delcol[i];

            res +=dfs(nrow,ncol,vis,grid);
        } 

        return res + grid[row][col];
    }
