class Solution {
public:
    int m, n;
    int iCount = 0;
    void floodfill(vector<vector<int>>& grid, int i, int j)
    {
        iCount+=grid[i][j];
        grid[i][j] = 0;
        if(i-1>=0 && grid[i-1][j] >0) floodfill(grid, i-1, j);
        if(i+1 < m && grid[i+1][j] >0) floodfill(grid, i+1, j);
        if(j-1>=0 && grid[i][j-1] >0) floodfill(grid, i, j-1);
        if(j+1 < n && grid[i][j+1] >0) floodfill(grid, i, j+1);
    }
    int findMaxFish(vector<vector<int>>& grid) {
        m=grid.size();
        n=grid[0].size();
        int iMaxCount = 0;
        for(int i=0; i<m; i++)
        {
            for(int j=0; j<n; j++)
            {
                if(grid[i][j] != 0)
                {
                    iCount = 0;
                    floodfill(grid, i, j);
                    if(iCount > iMaxCount) iMaxCount = iCount;

                }
            }
        }

        return iMaxCount;
    }
};
