int traverse(int** grid, int row, int col, int rLimit, int cLimit) {
    if(row < 0 || row >= rLimit || col < 0 || col >= cLimit || !grid[row][col]) return 0;
    int ret = grid[row][col];
    grid[row][col] = 0;

    ret += traverse(grid, row + 1, col, rLimit, cLimit);
    ret += traverse(grid, row - 1, col, rLimit, cLimit);
    ret += traverse(grid, row, col + 1, rLimit, cLimit);
    ret += traverse(grid, row, col - 1, rLimit, cLimit);

    return ret;
}

int findMaxFish(int** grid, int gridSize, int* gridColSize){
    int tmp = 0;
    int max = 0;

    for(int i = 0; i < gridSize; i++) {
        for(int j = 0; j < *gridColSize; j++) {
            if(grid[i][j]) {
                tmp = traverse(grid, i, j, gridSize, *gridColSize);
                max = (tmp > max)? tmp : max;
            }
        }
    }
    return max;
}

