    public int findMaxFish(int[][] matrix) {
        	int rows = matrix.length;
					int col = matrix[0].length;
					int maxFish = 0;
					boolean[][] visited = new boolean[rows][col];

					for (int i = 0; i < rows; i++) {
						for (int j = 0; j < col; j++) {
							if (matrix[i][j] > 0) {
								int fishByCell = bfs(i, j, matrix, matrix[i][j], visited);
								maxFish = Math.max(maxFish, fishByCell);
							}
						}
