 public static int bfs(int x, int y, int[][] matrix, int fish, boolean[][] visited) {
		int rows = matrix.length;
		int col = matrix[0].length;
		int maxFish = fish;
		visited[x][y] = true;
		Queue<Cellss> queue = new LinkedList<Cellss>();
		queue.add(new Cellss(x, y));
		while (!queue.isEmpty()) {
			Cellss curenCell = queue.poll();
			int cureentX = curenCell.x;
			int cureentY = curenCell.y;
			for (int i = 0; i < 4; i++) { 
				int nextX = cureentX + dx[i];
				int nextY = cureentY + dy[i]; 
				if (nextX < rows && nextX >= 0 && nextY < col && nextY >= 0
						&& matrix[nextX][nextY] > 0 && !visited[nextX][nextY]) {
					maxFish = maxFish + matrix[nextX][nextY];

					queue.add(new Cellss(nextX, nextY));
					visited[nextX][nextY] = true;
				}
			}
		}

		return maxFish;
	}
}


class Cellss {
	int x;
	int y;

	public Cellss(int x, int y) {
		this.x = x;
		this.y = y;
	}
}

