var findMaxFish = function(grid) {
  const dydx = [[0,1],[0,-1],[1,0],[-1,0]];
  const rows = grid.length;
  const cols = grid[0].length;
  const set = new Set();
  let max = 0;

  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < cols; j++) {
      if (grid[i][j] > 0) max = Math.max(grid[i][j] + dfs(i,j), max);
    }
  }
  return max;

  function dfs(i, j) {
    const key = `${i}:${j}`;
    if (set.has(key) || notValid(i, j)) return 0;
    set.add(key);

    let sum = 0;
    for (const [dy, dx] of dydx) {
      const [x, y] = [dx + i, dy + j];
      if (notValid(x,y) || set.has(`${x}:${y}`)) continue;
      sum += grid[x][y] + dfs(x,y,set);
    }
    return sum;
  }

  function notValid (i, j) {
    return (i < 0 || i >= rows || j < 0 || j >= cols || grid[i][j] === 0); 
  }
};
