# Code
```
public class Solution {
    public int FindMaxFish(int[][] grid) {

        int m = grid.Length;
        int n = grid[0].Length;

        bool[,] visited = new bool[m,n];
        int maxFishes = 0;

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
            {
                if(grid[i][j]!=0 && !visited[i,j])
                {
                    maxFishes = Math.Max(maxFishes,DFS(grid,i,j,visited));
                }
            }
        }
        return maxFishes;
    }

    private int DFS(int[][] grid,int i,int j, bool[,] visited)
    {

        int[] c = new int[]{1,0,-1,0};
        int[] r = new int[]{0,-1,0,1};
        int sum = grid[i][j];
        visited[i,j] = true;

        for(int k=0;k<4;k++)
        {
            int row = i+r[k];
            int col = j+c[k];
            if(isAllowed(row,col,grid.Length,grid[0].Length) && !visited[row,col] && grid[row][col]!=0)
            {
                sum = sum + DFS(grid,row,col,visited);
            }
        }
        return sum;
    }

    private bool isAllowed(int i,int j,int m,int n)
    {
        if(i>=0 && i< m && j>=0 && j<n)
            return true;

        return false;    
    }
}
