class Solution:
    def findMaxFish(self, grid: List[List[int]]) -> int:

        n, m = len(grid), len(grid[0])


        def dfs(i, j):
            if i < 0 or j < 0 or i >= n or j >= m:
                return 0

            if grid[i][j] == 0:
                return 0

            fish, grid[i][j] = grid[i][j], 0

            fish += dfs(i + 1, j)
            fish += dfs(i - 1, j)
            fish += dfs(i, j + 1)
            fish += dfs(i, j - 1)
            return fish


        ans = 0
        for i in range(n):
            for j in range(m):
                if grid[i][j] != 0:
                    ans = max(ans, dfs(i, j))

        return ans







