class Solution {
public:
    int findMaxFish(vector<vector<int>>& grid) {
        int mx=0,i=0,j=0,p=0,m=grid.size(),n=grid[0].size(),f=0,r=0,c=0,s=0,a=0,k=0;
        queue<int>q;
        for(i=0;i<m;i++)
            for(j=0;j<n;j++)
                if(grid[i][j]>0)
                {
                    a=0;
                    q.push(i*n+j);
                    while(q.size()>0)
                    {
                        s=q.size();
                        for(k=0;k<s;k++)
                        {
                            f=q.front();
                            r=f/n,c=f%n;
                            if(grid[r][c]>0)
                            {
                                a+=grid[r][c];
                                grid[r][c]=0;
                                if(r>0) q.push(f-n);
                                if(r<m-1) q.push(f+n);
                                if(c>0) q.push(f-1);
                                if(c<n-1) q.push(f+1);
                            }
                            q.pop();
                        }
                    }
                    mx=max(a,mx);
                }
        return mx;
    }
};
