    def findMaxFish(self, grid: List[List[int]]) -> int:
	    # visited keeps track of all the cells visited
        visited = set()

		#initialize rows and columns variables
        rows = len(grid)
        cols = len(grid[0])

        # DFS function
        def backtrack(i, j):
		    # Check if out of bounds
            if i < 0 or j < 0 or i == rows or j == cols:
                return 0
            else:
			    # If land the fish caught will be 0
                if grid[i][j] == 0:
                    return 0
				# If already visited don't take the path
                if (i, j) in visited:
                    return 0

				# Add the cell to visited
                visited.add((i, j))

				# Find out the total number of fish that can be caught by performing DFS recursively
                return grid[i][j] + backtrack(i, j + 1) + backtrack(i, j - 1) + backtrack(i+1, j) + backtrack(i-1,j)


        res = 0


		# Iterate over every cell of the grid
        for i in range(rows):
            for j in range(cols):
			   # If the grid is not land and it has been not visited so far, perform DFS on it and get the total sum as current value
                if (i, j) not in visited and grid[i][j] != 0:
                    current_value = backtrack(i, j)
					# If this path gives best result update the result variable
                    res = max(current_value, res)
        return res


