class Solution {
public:

    int r,c;
    vector<int>parents;

    bool isValid(int x, int y, vector<vector<int>>&grid){
        if((x>=0 && x<r) && (y>=0 && y<c) && grid[x][y])
            return true;
        return false;
    }

    int getAbsoluteParent(int n){
        if(n!=parents[n])
            parents[n] = getAbsoluteParent(parents[n]);

        return parents[n];
    }

    void unify(int x, int y){
        int px = getAbsoluteParent(x);
        int py = getAbsoluteParent(y);

        if(px!=py)
            parents[px] = py;
    }

    int findMaxFish(vector<vector<int>>& grid) {
        r = grid.size();
        c = grid[0].size();
        vector<pair<int,int>>dirs{{-1,0},{1,0},{0,-1},{0,1}};
        vector<int>totalFishes(r*c,0);
        parents = vector<int>(r*c);

        for(int i=0;i<r*c;i++)
            parents[i] = i;

        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){
                if(!grid[i][j])
                    continue;

                for(int k=0;k<4;k++){
                    int ni = i + dirs[k].first;
                    int nj = j + dirs[k].second;
                    if(isValid(ni,nj,grid)){
                        unify(ni*c+nj,i*c+j);
                    }
                }
            }
        }

        int ans = 0;
        for(int i=0;i<r;i++){
            for(int j=0;j<c;j++){

                if(grid[i][j]){
                    int cur_parent = getAbsoluteParent(i*c+j);
                    totalFishes[cur_parent] += grid[i][j];
                    ans = max(ans,totalFishes[cur_parent]);
                }

            }
        } 
        return ans;
    }
};
