    if(grid[r][c] === 0) return 0;

    let fish = grid[r][c];
    grid[r][c] = 0;

    return fish + dfs(grid, r - 1, c) + dfs(grid, r, c - 1) + dfs(grid, r + 1, c) + dfs(grid, r, c + 1);
}
