class Solution {
    void dfs(int row, int col, vector<vector<int>>& grid, vector<vector<int>>& vis, int &fish) {
        int n = grid.size(), m = grid[0].size(); 
        vis[row][col] = 1;
        fish += grid[row][col];
        int r_dir[4] = {-1, 0, 1, 0};
        int c_dir[4] = {0, 1, 0, -1};
        for(int i = 0; i < 4; i++) {
            int r = row + r_dir[i];
            int c = col + c_dir[i];
            if(r >= 0 && r < n && c >= 0 && c < m && grid[r][c] && !vis[r][c]) {
                dfs(r, c, grid, vis, fish);
            }
        }
    }
public:
    int findMaxFish(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<vector<int>> vis(n, vector<int>(m, 0));
        int max_fish = 0;
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                if(grid[i][j] > 0 && !vis[i][j]) {
                    int fish = 0;
                    dfs(i, j, grid, vis, fish);
                    max_fish = max(max_fish, fish);
                }
            }
        }
        return max_fish;
    }
};
