class Solution {
public:
    int dfs(vector<vector<int>>& grid, int x, int y, vector<pair<int,int>>& dir){
        int currFish = grid[x][y];
        grid[x][y] = 0;

        for(auto& d: dir){
            int nx = x + d.first;
            int ny = y + d.second;

            if(nx >= 0 && ny >= 0 && nx < grid.size() && ny < grid[0].size() && grid[nx][ny] != 0){
                currFish += dfs(grid, nx, ny, dir);
            }
        }

        return currFish;
    }
    int findMaxFish(vector<vector<int>>& grid) {
        vector<pair<int,int>> dir{{0,1},{1,0},{-1,0},{0,-1}};
        int ans = 0;
        for(int i = 0; i < grid.size(); i++){
            for(int j = 0; j < grid[0].size(); j++){
                if(grid[i][j] != 0){
                    int currFish = dfs(grid, i, j, dir);
                    ans = max(ans, currFish);
                }
            }
        }
        return ans;
    }
};

