class Solution 
{
public:
    int dfs(vector<vector<int>>& grid, int r, int c)
    {
        if(r<0 || r>=grid.size() || c<0 || c>=grid[0].size() || grid[r][c] < 1)
            return 0;
        grid[r][c] *= -1;
        return abs(grid[r][c]) + dfs(grid,r,c-1) + dfs(grid,r-1,c) + dfs(grid,r,c+1) + dfs(grid,r+1,c);
    }

    int findMaxFish(vector<vector<int>>& grid) 
    {
        int row = grid.size(), col = grid[0].size(), sum = 0;
        for(int r=0; r<row; r++)
            for(int c=0; c<col; c++)
                if(grid[r][c]>0)
                    sum = max(sum, dfs(grid,r,c));
        return sum;
    }
};
