class Solution {
public:
    bool isvalid(vector<vector<int>>& grid, int r, int c)
    {
        if(r>=0 && c>=0 && r<grid.size() && c<grid[0].size() && grid[r][c]!=0)
        {
            return true;
        }
        return false;
    }
    int dfs(vector<vector<int>>& grid, int i, int j, int &sum)
    {
        sum+=grid[i][j];
        grid[i][j]=0;
        if(isvalid(grid, i+1, j))
        {
            dfs(grid, i+1, j, sum);
        }
        if(isvalid(grid, i-1, j))
        {
            dfs(grid, i-1, j, sum);
        }
        if(isvalid(grid, i, j+1))
        {
            dfs(grid, i, j+1, sum);
        }
        if(isvalid(grid, i, j-1))
        {
            dfs(grid, i, j-1, sum);
        }
        return sum;

    }
    int findMaxFish(vector<vector<int>>& grid) {
        ios::sync_with_stdio;
        cin.tie(0);
        int m=0;
        for(int i=0; i<grid.size(); i++)
        {
            for(int j=0; j<grid[0].size(); j++)
            {
                int sum=0;
                if(grid[i][j]!=0)
                {
                    int x = dfs(grid, i, j, sum);
                    m = max(m, x);
                }
            }
        }
        return m;

    }
};
