class Solution {
    public int findMaxFish(int[][] grid) {
        int ans = 0;
        int m = grid.length, n = grid[0].length;
        int[][] dirs = new int[][]{{1, 0}, {-1, 0}, {0, 1}, {0, -1}};
        UnionFind dsu = new UnionFind(grid);

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (grid[i][j] == 0) continue;

                for (int[] dir : dirs) {
                    int x = i + dir[0], y = j + dir[1];
                    if (x < 0 || x >= m || y < 0 || y >= n || grid[x][y] == 0)
                        continue;
                    dsu.union(i, j, x, y);
                }
            }
        }

        for (int i = 0; i < m * n; i++) 
            if (dsu.id[i] == i) ans = Math.max(ans, dsu.sum[i]);

        return ans;
    }
}

class UnionFind {
    int[] id, sum, rank;
    int m, n;

    public UnionFind(int[][] grid) {
        this.m = grid.length;
        this.n = grid[0].length;
        id = new int[m*n];
        sum = new int[m*n];
        rank = new int[m*n];
        for (int i = 0; i < m*n; i++) {
            id[i] = i; 
            rank[i] = 1;
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) 
                sum[i * n + j] = grid[i][j];
        }
    }

    public int find(int x) {
        int root = x;
        while (id[root] != root)
            root = id[root];

        return root;
    }

    public void union(int i, int j, int x, int y) {
        int rootX = find(i * n + j), rootY = find(x * n + y);
        if (rootX == rootY) return;

        if (rank[rootX] >= rank[rootY]) {
            rank[rootX] += rank[rootY];
            rank[rootY] = 0;
            id[rootY] = rootX;
            sum[rootX] += sum[rootY];
        } else {
            rank[rootY] += rank[rootX];
            rank[rootX] = 0;
            id[rootX] = rootY;
            sum[rootY] += sum[rootX];
        }

    }
}
