public class Solution {
    int[] rank;
    int[] parent;

    public int FindMaxFish(int[][] grid) {
        var max_fish = 0;
        var rowCount = grid.GetLength(0);
        var columnCount = grid[0].Length;

        rank = new int[rowCount * columnCount];
        parent = new int[rowCount * columnCount];

        for(int row =  0; row < rowCount; row++) {
            for(int col = 0; col < columnCount; col++) {
                var idx = row * columnCount + col;
                parent[idx] = idx;
                rank[idx] = grid[row][col];
                max_fish = Math.Max(max_fish, rank[idx]);
            }
        }

        for (int row = 0; row < rowCount; row++) {
            for (int col = 0; col < columnCount; col++) {
                if (grid[row][col] != 0) {
                    var idx = row * columnCount + col;
                    if(row < rowCount - 1 && grid[row + 1][col] != 0)
                        max_fish = Math.Max(max_fish, Union(idx, (row + 1) * columnCount + col));

                    if(col < columnCount - 1 && grid[row][col + 1] != 0)
                        max_fish = Math.Max(max_fish, Union(idx, row * columnCount + (col + 1)));
                }
            }
        }

        return max_fish;
    }

    private int FindParent(int node) {
        if (parent[node] == node)
            return parent[node];

        parent[node] = FindParent(parent[node]);
        return parent[node];
    }

    private int Union(int node1, int node2) {
        var parent1 = FindParent(node1);
        var parent2 = FindParent(node2);

        if(parent1 != parent2) {
            if (parent1 < parent2) {
                rank[parent1] += rank[parent2];
                parent[parent2] = parent1;
                return rank[parent1];
            }
            else {
                rank[parent2] += rank[parent1];
                parent[parent1] = parent2;
                return rank[parent2];
            }
        }

        return 0;
    }
}
