class Solution {
public:
    void dfs(int r , int c , vector<vector<int>>& grid , vector<vector<int>>& vis , vector<int> delRow , vector<int> delCol , int m , int n , int& sum){
        sum += grid[r][c];
        vis[r][c] = 1;

        for (int i=0 ; i<4 ; i++){
            int nr = r + delRow[i];
            int nc = c + delCol[i];

            if (nr>=0 && nr<m && nc>=0 && nc<n && !vis[nr][nc] && grid[nr][nc]>0){
                dfs(nr , nc , grid, vis , delRow , delCol , m , n , sum);
            }
        }
    }

    int findMaxFish(vector<vector<int>>& grid) {
        int maxi=-1e9;

        int m = grid.size();
