class Solution {
public:
    int dfs(int i, int j, vector<vector<bool>>&visited, vector<vector<int>>&grid)
    {
        int ans=grid[i][j];
        visited[i][j]=true;
        if(i+1<grid.size() && !visited[i+1][j] && grid[i+1][j]!=0) ans = ans + dfs(i+1,j,visited,grid);
        if(j+1<grid[0].size() && !visited[i][j+1] && grid[i][j+1]!=0) ans = ans + dfs(i,j+1,visited,grid);
        if(i-1>=0 && !visited[i-1][j] && grid[i-1][j]!=0) ans = ans + dfs(i-1,j,visited,grid);
        if(j-1>=0 && !visited[i][j-1] && grid[i][j-1]!=0) ans = ans + dfs(i,j-1,visited,grid);
        return ans;

    }
    int findMaxFish(vector<vector<int>>& grid) 
    {
        int m = grid.size();
        int n = grid[0].size();
        vector<vector<bool>>visited(m,vector<bool>(n,false));
        int ans=0;
        for(int i =0;i<m;i++)
        {
            for(int j =0;j<n;j++)
            {
                if(!visited[i][j] && grid[i][j]!=0)
                {
                    ans=max(ans,dfs(i,j,visited,grid));
                }
            }
        }
        return ans;
    }
};
