class Solution {
public:
    int findparent(int node, vector<pair<int,int>>&parent)
    {
        if(node==parent[node].first) return node;
        return parent[node].first=findparent(parent[node].first,parent);
    }
    void Union(int u, int v, vector<pair<int,int>>&parent, vector<int>&rank)
    {
        int pu = findparent(u,parent);
        int pv = findparent(v, parent);
        if(pu==pv) return;
        else if(rank[pu]<rank[pv]) 
        {
            parent[pu].first = pv;
            parent[pv].second +=parent[pu].second;
        }
        else if(rank[pv]<rank[pu]) 
        {
            parent[pv].first =pu;
            parent[pu].second +=parent[pv].second;
        }
        else
        {
            parent[pv].first=pu;
            parent[pu].second +=parent[pv].second;
            rank[pu]++;
        }
    }
    bool isvalid(int i, int j, int m, int n)
    {
        if(i>=0 && i<m && j>=0 && j<n) return true;
        return false;
    }
    int findMaxFish(vector<vector<int>>& grid) 
    {
         std::ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        cout.tie(NULL);
        int m = grid.size();
        int n = grid[0].size();
        vector<pair<int,int>>parent(m*n);
        vector<int>rank(m*n,0);
        for(int i =0;i<(m*n);i++)
        {
            int a = i/n;
            int b = i%n;
            parent[i]={i,grid[a][b]};
        }
        for(int a=0;a<(m*n);a++)
        {
            int i  = a/n;
            int j = a%n;
            if(grid[i][j]!=0)
            {
                if(isvalid(i+1,j,m,n) && grid[i+1][j]!=0)
                {
                    int b = (i+1)*n+j;
                    Union(a,b,parent, rank);
                }
                if(isvalid(i-1,j,m,n) && grid[i-1][j]!=0)
                {
                    int b = (i-1)*n+j;
                    Union(a,b,parent, rank);
                }
                if(isvalid(i,j+1,m,n) && grid[i][j+1]!=0)
                {
                    int b = (i)*n+(j+1);
                    Union(a,b,parent, rank);
                }
                if(isvalid(i,j-1,m,n) && grid[i][j-1]!=0)
                {
                    int b = (i)*n+(j-1);
                    Union(a,b,parent, rank);
                }
            }
        }
        int ans=0;
        for(int i =0;i<parent.size();i++)
        {
            ans=max(ans,parent[i].second);
        }
        return ans;

    }
};
