class Solution {
public:

    int bfs(int row,int col,vector<vector<int>>&grid,vector<vector<int>>&visited)
    {
        int count =0;
        int m = grid.size();
        int n = grid[0].size();
        queue<pair<int,int>>q;
        q.push({row,col});
        visited[row][col]=1;
        int del_row[]= {-1,0,1,0};
        int del_col[] = {0,1,0,-1};
        while(!q.empty())
        {

            int ro = q.front().first;
            int cl = q.front().second;


            count +=grid[ro][cl];
            q.pop();
            for(int i=0;i<4;i++)
            {

                int ne_row = ro+del_row[i];
                int ne_col = cl+del_col[i];
                if(ne_row<m && ne_row>=0 && ne_col <n && ne_col>=0 && !visited[ne_row][ne_col] && grid[ne_row][ne_col]!=0)
                {

                    visited[ne_row][ne_col]=1;
                    q.push({ne_row,ne_col});

                }
            }
        }
        return count;
    }

    int findMaxFish(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        vector<int>fishes;
        vector<vector<int>>visited(m,vector<int>(n,0));
