func findMaxFish(grid [][]int) int {
  m, n := len(grid), len(grid[0])
  var res int
  for i := 0; i < m; i++ {
    for j := 0; j < n; j++ {
      if grid[i][j] != 0 {
        res = max(res, dfs(grid, i, j))
      }
    }
  }
  return res
}

func dfs(grid [][]int, row, col int) int {
  res := grid[row][col]
  grid[row][col] = 0
  directions := [4][2]int{{-1, 0}, {0, -1}, {0, 1}, {1, 0}}
  for _, dir := range directions {
    x, y := row + dir[0], col + dir[1]
    if x >= 0 && x < len(grid) && y >= 0 && y < len(grid[x]) && grid[x][y] > 0 {
      res += dfs(grid, x, y)
    }
  }
  return res
}

func max(a, b int) int {
  if a > b {
    return a
  }
  return b
}
