typedef struct
{
    int **grid;
    int x;
    int y;
    int **visited;
}stGrid;

void recrusion(stGrid *field,int cur_x, int cur_y, int *cur_greb, int *maxFish)
{
    if((cur_x < 0 || cur_x >= field->x) || (cur_y < 0 || cur_y >= field->y) || 
                field->grid[cur_y][cur_x] == 0 || field->visited[cur_y][cur_x])
    {
        *maxFish = (*cur_greb>*maxFish)?(*cur_greb):(*maxFish);
    }
    else
    {
        if(field->visited[cur_y][cur_x] != 1)
        {
            *cur_greb += field->grid[cur_y][cur_x];
        }
        field->visited[cur_y][cur_x] = 1;
        recrusion(field,cur_x + 1, cur_y, cur_greb, maxFish);
        recrusion(field,cur_x, cur_y - 1, cur_greb, maxFish);
        recrusion(field,cur_x - 1, cur_y, cur_greb, maxFish);
        recrusion(field,cur_x, cur_y + 1, cur_greb, maxFish);
    }
}



int findMaxFish(int** grid, int gridSize, int* gridColSize){

    stGrid field;
    field.grid = grid;
    field.y = gridSize;
    field.x = *gridColSize;
    field.visited = (int**)calloc(field.y, sizeof(int*)); 
    for(int i = 0; i < field.y; i++)
    {
        field.visited[i] = (int*)calloc(field.x, sizeof(int));
    }

    int maxFish = 0;
    int temp_Fish = 0;
    for(int row = 0; row < field.y ; row++)
    {
        for(int col = 0; col < field.x; col++)
        {
            recrusion(&field, col, row, &temp_Fish, &maxFish);
            temp_Fish = 0;
        }
    }

    return maxFish;
}
