# Code
```
class Solution {
public:
    int di[4] = {1, -1, 0, 0}, dj[4] = {0, 0, 1, -1};

    bool ok(int i, int j, vector<vector<int>>& grid) {
        return i >= 0 && i < grid.size() &&
               j >= 0 && j < grid[0].size() &&
               grid[i][j] != 0;
    }

    int dfs(int i, int j, vector<vector<int>>& grid) {
        int res = grid[i][j];
        grid[i][j] = 0;
        for (int k = 0; k < 4; k++) {
            int r = i + di[k];
            int c = j + dj[k];
            if (ok(r, c, grid)) res += dfs(r, c, grid);
        }
        return res;
    }

    int findMaxFish(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        int ans = 0;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (grid[i][j] == 0) continue;
                int cnt = dfs(i, j, grid);
                ans = max(ans, cnt);
            }
        }
        return ans;
    }
};
