        int x=dfs(2*i,cost);
        int y=dfs(2*i+1,cost);
        ans+=abs(x-y);
        return max(x,y)+cost[i-1];
    }
    int minIncrements(int n, vector<int>& cost) {
        int x=dfs(1,cost);
        return ans;
    }
};
