                    mp[parent]=mp[curr]+cost[curr-1];

                    q.push(parent);
                }else{
                    ans+=abs(mp[parent]-(mp[curr]+cost[curr-1]));
                    mp[parent]=max(mp[parent],mp[curr]+cost[curr-1]);

                }



            }
        }
        return ans;
    }
};
