class Solution {
public:
    int ans=0;
    int minIncrements(int& n, vector<int>& cost,int i=0) {
        if(2*i+1>=n) return i==0?ans:cost[i];
        int x = minIncrements(n,cost,2*i+1);
        int y = minIncrements(n,cost,2*i+2);
        ans+=abs(x-y);
        return i==0?ans:max(x,y)+cost[i];
    }
};
