class Solution {
public:
    int ans = 0;
    int max_sum = INT_MIN;

    int dfs1(vector<int>& cost, int root, int n) {
        if (2 * root > n) {
            return cost[root - 1];
        }
        int left = dfs1(cost, 2 * root, n);
        int right = dfs1(cost, 2 * root + 1, n);
        return max(left, right) + cost[root - 1];
    }

    int dfs(vector<int>& cost, int root, int curr_sum, int max_sum, int n) {
        if (2 * root > n) {
            return max_sum - curr_sum - cost[root - 1];
        }
        int left = dfs(cost, 2 * root, curr_sum + cost[root - 1], max_sum, n);
        int right = dfs(cost, 2 * root + 1, curr_sum + cost[root - 1], max_sum, n);
        ans += abs(left - right);
        return min(left, right);
    }

    int minIncrements(int n,vector<int>& nums) {

        max_sum = dfs1(nums, 1, n);
        dfs(nums, 1, 0, max_sum, n);
        return ans;
    }
};

