The idea is that you can DFS the binary tree and calculate the cost to make every path equal to the most expensive one. For every node, you can subtract the minimum of its children from the answer as you could have incremented that node instead and saved some moves.


```
class Solution {
public:
    typedef array<int, 2> ai;
    ai dfs(int pos, int target, vector<int>& sum){
        if(pos*2 >= cost.size()) return {target-sum[pos-1],target-sum[pos-1]};
        int ans = 0;
        auto a1 = dfs(pos*2, target, sum);
        auto a2 = dfs(pos*2+1, target, sum);
        int mn = min(a1[1], a2[1]);
        ans += a1[0] + a2[0];
        ans -= mn;
        return {ans, mn};
    }
    int minIncrements(int n, vector<int>& cost) {
        vector<int> sum(cost.size(), 0);
        queue<ai> q;
        q.push({1, 0});
        while(!q.empty()){ // precomputed sums array, can also do it in the dfs if you want to
            auto [id, d] = q.front();
            q.pop();
            if(id > n) continue;
            d += cost[id-1];
            sum[id-1] = d;
            q.push({id*2, d});
            q.push({id*2+1, d});
        }
        int target = *max_element(sum.begin(), sum.end());
        return dfs(1, cost, target, sum)[0];
    }
};

