class Solution {
public:
    int func(int node, int n, vector<int>&cost){
        if(node>=n)return 0;
        return cost[node]+max(func(2*node+1,n,cost), func(2*node+2,n,cost));
    }

    int ans(int node,int currcost, int maxcost, int n, vector<int>&cost, int&already){
        if(node>=n)return abs(maxcost-currcost);
        int left = ans(node*2+1,cost[node]+currcost,maxcost,n,cost,already);
        int right = ans(node*2+2,cost[node]+currcost,maxcost,n,cost,already);
        already+=(abs(left-right));
        return min(left,right);
