public class Solution {
    int max_sum, min_increment;
    public int MinIncrements(int n, int[] cost) {
        max_sum = 0;
        min_increment = 0;

        CalculateRootToLeafMax(cost, 0, 0, 0);
        MinIncrementsUntil(cost, 0, 0, 0);

        return min_increment;
    }

    private int MinIncrementsUntil(int[] cost, int level, int idx, int sum) {
        if(idx >= cost.Length)
            return max_sum - sum;

        var child = idx << 1;
        var currSum = sum + cost[idx];

        var left = MinIncrementsUntil(cost, 1 + level, child + 1, currSum);
        var right = MinIncrementsUntil(cost, 1 + level, child + 2, currSum);

        var temp = Math.Min(left, right);

        min_increment = min_increment + (left - temp) + (right - temp);

        return temp;
    }

    private void CalculateRootToLeafMax(int[] cost, int level, int idx, int sum) {
        if(idx >= cost.Length) {
            max_sum = Math.Max(max_sum, sum);
            return;
        }

        var child = idx << 1;
        var currSum = sum + cost[idx];

        CalculateRootToLeafMax(cost, 1 + level, child + 1, currSum);
        CalculateRootToLeafMax(cost, 1 + level, child + 2, currSum);
    }
}
