class Solution {
public:
    int minIncrements(int n, vector<int>& cost) {
        int ans =0 ;
        for(int i=(n/2 - 1) ;i>=0;i--) {
            int index = i+1;
            int leftCost = cost[2*index-1] ;
            int rightCost = cost[2*index];
            if(leftCost < rightCost) {
                ans += rightCost - leftCost;
                cost[2*index-1] = rightCost;
            }
            else if(leftCost > rightCost) {
                ans += leftCost - rightCost;
                cost[2*index] = leftCost;
            }
            cost[index-1] += cost[2*index];
        }
        return ans;

    }
};
