from functools import cache


class Solution:

    def minIncrements(self, n: int, cost: list[int]) -> int:
        @cache
        def post_order(i: int) -> tuple[int, int]:
            """
            :param i: node id
            :return: total changes up to node "i", path sum from leaf to node "i"
            """
            if i >= n:
                return 0, 0
            else:
                (l_inc, l_path_sum), (r_inc, r_path_sum) = post_order(2 * i + 1), post_order(2 * i + 2)

                inc = l_inc + abs(l_path_sum - r_path_sum) + r_inc
                path_sum = cost[i] + max(l_path_sum, r_path_sum)

                return inc, path_sum

        return post_order(0)[0]

