class Solution {
public:

    void fun(int & ans,int i,int n,vector<int>& cost){
        if(i == 0){
            //ans = ans + abs(cost[2*i] - cost[2*i + 1]);
            return;
        }
        else{
            if(2* i > n && 2*i + 1 > n){ // for leaf node
                if(cost[i] >= cost[i-1]){
                    ans = ans + cost[i] - cost[i-1];
                    //cout<<ans<<"leaf ";
                }
                else{
                    ans = ans + cost[i-1] - cost[i];
                    //cout<<ans<<"leaf ";
