            else if(2*i < n && 2*i + 1 < n ){
                int right = cost[2*i + 1] + cost[i];

                int left = cost[2*(i-1) + 1] + cost[i-1];

                //cout<<right<<" "<<left<<"\n";

                cost[i] = right;

                cost[i-1] = left;

                if(cost[i] >= cost[i-1]){
