        int inc=sum-dp[i];
        ans+=inc;
     solve(2*i+1,sum-inc-cost[i],ans,dp,cost,n);
     solve(2*i+2,sum-inc-cost[i],ans,dp,cost,n);
    }
    int minIncrements(int n, vector<int>&cost) {
        vector<int>dp(n);
        int p=log2(n);
        for(int i=n-1;i>(n-1-(1<<p));i--)dp[i]=cost[i];
        int i=n-1;
        while(i>0){
                dp[(i-1)/2]=max(dp[i],dp[i-1])+cost[(i-1)/2];
                i-=2;
        }
        int ans=0;
        solve(0,dp[0],ans,dp,cost,n);
        return ans;
    }
