class Solution {
    int increments;
    private int back(int[]cost,int root){
         if(root>cost.length)return 0;
         int left=2*root; //calculate left and right subtree cost
         int right=left+1; 

         int lsum=back(cost,left);
         int rsum=back(cost,right);
         increments+=Math.abs(lsum-rsum);//adding the  number of operations required to equalize the cost of both subtrees 
         return cost[root-1]+Math.max(lsum,rsum);  

    }
    public int minIncrements(int n, int[] cost) {
      increments=0;
      back(cost,1);
      return increments;
    }
}
