class Solution {
public:
    int final_cost = 0;
    int recurs(int n, vector<int>& cost,int i)
    {
        if(2*i>n)
            return cost[i-1];

        int left = recurs(n,cost,2*i);
        int right = recurs(n,cost,2*i+1);

        // cout<<i<<" "<<left<<" "<<right<<endl;

        int m = max(left,right);
        final_cost += m-left+m-right;
        return m+cost[i-1];       
    }

    int minIncrements(int n, vector<int>& cost) {
        final_cost = 0;
        recurs(n,cost,1);
        return final_cost;
    }
};
