class Solution {
public:

    int solve(int node,vector<int> &cost,int n,int &ans){
        int child1 = 2*node + 1;
        int child2 = 2*node + 2;

        if(child1 >= n && child2 >= n){
            //Leaf
            return cost[node];
        }

        int left_cost_needed = 0, right_cost_needed = 0;

        left_cost_needed = solve(child1,cost,n,ans);

        right_cost_needed = solve(child2,cost,n,ans);

        ans = ans + abs(left_cost_needed-right_cost_needed);

        //Updated Cost For Tree from node till ALL leafs
        return cost[node] + max(left_cost_needed,right_cost_needed);

    }

    int minIncrements(int n, vector<int>& cost) {
       //Perfect Tree => Two Childs OR Zero
        int ans = 0;

        solve(0,cost,n,ans);

        return ans;

    }

};
```

