class Solution {
public:
    int minIncrements(int n, vector<int>& cost) {
        int len = cost.size();
        for (int i = 0; i < len; i++) {
            if (2 * i + 1 < len) {
                cost[2 * i + 1] += cost[i];
            }
            if (2 * i + 2 < len) {
                cost[2 * i + 2] += cost[i];
            }
        }
        int mxValue = *max_element(cost.begin(), cost.end());
        vector<int> mx(len);
        for (int i = len - 1; i > len / 2 - 1; i--) {
            mx[i] = cost[i];
        }
        for (int i = len / 2 - 1; i >= 0; i--) {
           mx[i] = max(mx[2 * i + 1], mx[2 * i + 2]);
        }
        vector<int> down(len);
        int sol = 0;
        for (int i = 0; i < n; i++) {
            int curValue = mx[i] + down[i];
            if (curValue < mxValue) {
                int diff = mxValue - curValue;
                sol += diff;
                down[i] += diff;
