class Solution {
public:
    int res = 0;
    int minIncrements(int n, vector<int>& cost) {
        DFS(1,cost);
        return res;
    }


    int DFS(int node, vector<int>& cost){
        //basecase
        int n = cost.size();
        if(node>n){
            return 0;
        }

        int currCost = cost[node-1];

        int left = DFS(node*2, cost);
        int right = DFS(node*2+1, cost);

        res+= abs(left-right);

        return currCost + max(left, right);
    }
};

