class Solution {
public:
    int m;
    vector<long long>dp;
    int ans;
    long long  dfs1(int i,vector<int>&cost)
    {
        if(i>m)return 0;
        dp[i-1]=max(dfs1(2*i,cost),dfs1(2*i+1,cost));
        return dp[i-1]+cost[i-1];
    }

    void dfs(int x,long long  prev,vector<int>&cost,int i)
    {
        if(i>m)return ;
        long long  total=cost[i-1]+dp[i-1]+prev;
        long long  incre=x-total;
        ans+=(int)incre;
        cost[i-1]+=(int)incre;
        dfs(x,prev+cost[i-1],cost,2*i);
         dfs(x,prev+cost[i-1],cost,2*i+1);
        return ;
    }

    int minIncrements(int n, vector<int>& cost) {
        m=n;
        dp.clear();
        dp=vector<long long>(n+1);
        vector<int>prevcost=cost;
        ans=0;
        for(int i=0;i<n;i++)
        {
            dp[i]=(long long)cost[i];
        }
        int x=(int)dfs1(1,cost);

         dfs(x,0,cost,1);
        return ans;
    }
};
