    int dfs2(int node , int value,int cost[]){

        if( node >= cost.length ) return 0;

         int temp = value - max[node];

          if( temp < 0 )  return -1;

          int left = dfs2(2*node+1,value - cost[node] - temp , cost);

            if( left == -1 ) return -1;

          int right  =dfs2(2*node+2,value-cost[node]-temp,cost);

        if(right == -1) return -1;

        return left + right + temp;
    }

