class Solution {
public:

    int res = 0, n;

    int dfs(int ind, vector<int> &cost){
        if(ind > size(cost)) return 0;
        int left = dfs(2 * ind, cost), right = dfs(2 * ind + 1, cost);
        res += abs(left - right);
        return cost[ind - 1] + max(left, right);
    }

    int minIncrements(int n, vector<int>& cost) {
        dfs(1, cost);
        return res;
    }
};
```
