class Solution {
    int ans;
    public int minIncrements(int n, int[] cost) {
        ans=0;
        int max=findMax(cost,0);//first find the path which has max path length
        helper(max,cost,0,0);
        return ans;
    }
    int helper(int max,int cost[],int root,int sum){
        int n=cost.length,diff=0,min=0;
        if(2*root+1>=n)
            return max-sum-cost[root];//leaf node case just return the diff btween path sum and max
        else{
            sum+=cost[root];
            int left=helper(max,cost,2*root+1,sum);
            int right=helper(max,cost,2*root+2,sum);
            min=Math.min(left,right);
            diff=Math.abs(left-right);//the diff can be adjusted in the same level
            //minimum of both can be sent to upper level to minimise the total cost.
        }
        ans+=diff;//adjust the difference on the same level
        return min;//send the min to the upper level
    }
    int findMax(int cost[],int root){
        //fxn to find the max path length
        int n=cost.length;
        if(root>=n)
            return 0;
        return cost[root]+Math.max(findMax(cost,2*root+1),findMax(cost,2*root+2));
    }
}
