class Solution {
private:
    int isPowerOfTwo(int n){
        if (n == 0)
            return -1;
        int x=ceil(log2(n));
        int y=floor(log2(n));

        if(x==y) return x;
        else return -1;
    }
    int inOrder(int root,int h,vector<int>&cost,int &ans){
        if(h==0){
            return 0;
        }

        int leftCost=inOrder(2*root,h-1,cost,ans);
        int rootCost=cost[root-1];
        int rightCost=inOrder(2*root+1,h-1,cost,ans);

        if(leftCost!=rightCost){
            ans+=abs(leftCost-rightCost);
        }

        return max(leftCost,rightCost)+rootCost;
    }
public:
    int minIncrements(int n, vector<int>& cost) {

        int height= isPowerOfTwo(n+1);

        int ans=0;
        inOrder(1,height,cost,ans);
        return ans;
    }
};
```

