class Solution:
    def minIncrements(self, n: int, cost: List[int]) -> int:

        # get maximum path sum
        def dfs(i):
            if 2 * i > n:
                return cost[i-1]
            return cost[i-1] + max(dfs(2*i), dfs(2*i+1))

        max_v = dfs(1)


        # calculate the path sum for each leaf node 
        leaf_sum = {}
        def dfs2(i, v):
            if 2 * i > n:
                leaf_sum[i] = cost[i-1] + v
                return
            dfs2(2*i, v+cost[i-1])
            dfs2(2*i+1, v+cost[i-1])


        dfs2(1, 0)

        # up propagate the max leaf path sum to its parent, which means the minimum cost to travel to the leaf
        node_max = defaultdict(int)
        for i in range((n + 1) // 2, n+1):
            j = i
            while j > 0:
                node_max[j] = max(node_max[j], leaf_sum[i])
                j //= 2

        # for each node, calculate the increments needed to reach the leaf node and accumulate the increments
        self.ans = 0
        def dfs3(i, v):
            if i > n:
                return
            diff = max_v - (node_max[i] + v)
            self.ans += diff
            dfs3(2*i, v+diff)
            dfs3(2*i+1, v+diff)

        dfs3(1, 0)

        return self.ans

